  static List<TarotCard> getAllCards() {
    return allCards.map((card) => TarotCard.fromMap(card)).toList();
  }

  static List<TarotCard> getMajorArcana() {
    return getAllCards().where((card) => card.isMajorArcana).toList();
  }

  static List<TarotCard> getMinorArcana() {
    return getAllCards().where((card) => card.isMinorArcana).toList();
  }

  static List<TarotCard> getCardsBySuit(String suit) {
    return getAllCards().where((card) => card.suit == suit).toList();
  }

  static TarotCard? getCardById(int id) {
    try {
      return getAllCards().firstWhere((card) => card.id == id);
    } catch (e) {
      return null;
    }
  }

  static List<TarotCard> searchCards(String query) {
    final lowercaseQuery = query.toLowerCase();
    return getAllCards().where((card) =>
        card.name.toLowerCase().contains(lowercaseQuery) ||
        card.animal.toLowerCase().contains(lowercaseQuery) ||
        card.keywords.toLowerCase().contains(lowercaseQuery)).toList();
  }
} 